
var mongoose = require('mongoose');
var Schema = mongoose.Schema;

 
var userSchema = mongoose.Schema({
    token : String,
	name : {type: String, required : true},
	lastname : String,
	typeuser : { type: String, enum: ['Alumno', 'Profesor']},
    email: { type: String, required : true, unique : true},
    hashed_password: {type: String,required : true},
    salt : String,
    temp_str:String,
	
	publickey : String,
	//privatekey : String
});
 

//mongoose.connect('mongodb://localhost:27017/node-android');
//mongoose.connect('mongodb://myUser:userlist@localhost:27017/node-android');
//mongoose.connect('mongodb://localhost:27017/sampledb');

module.exports = mongoose.model('users', userSchema);


